import React, { useState } from 'react';

function LoginPage(props) {
  const [username, setUsername] = useState('');
  const onChange = (evt) => setUsername(evt.target.value);

  const onClick = () => {
    // perform username validation here
    if (isValidUsername(username)) {
      props.onLogin(username);
    } else {
      alert('Invalid username. Please enter a valid username.');
    }
  }

  const isValidUsername = (username) => {
    // perform any validation checks here
    return username.length > 0;
  }

  return (
    <div className="chat-login-page-container">
      <div className="chat-login-page-controls-container">
        <h4>Set your username</h4>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={onChange}
        />
        <button
          type="button"
          onClick={onClick}
          disabled={!isValidUsername(username)}
        >
          Login
        </button>
      </div>
    </div>
  );
}

export default LoginPage;
